sys_variable_value.value:
var id = workflow.scratchpad.configuration_id.toString();
var gr = new GlideRecord('x_snc_skytap_demo_variable_label_mapping');
gr.addEncodedQuery('variable.cat_item=' + current.cat_item);
gr.query();
while(gr.next())
	{
	var vars = {};
	vars['u_label_category'] = gr.label_category.getDisplayValue();
	vars['u_value'] = current.variables[gr.variable.name];
	vars['u_scheduleid'] = id;
	var w = new global.Workflow();
	var context = w.startFlow(w.getWorkflowFromName(gs.getProperty('x_snc_skytap_demo.skytap.workflow.labeltoenvironment.id')), current, current.operation(), vars);
}

var response = activity.output;
var jsonparse = new global.JSON();
var jsonLabel = jsonparse.decode(response);
gs.info('Mani ' + jsonLabel['id']);
workflow.scratchpad.scheduleid = jsonLabel['id'];

var response = activity.output;
var jsonparse = new global.JSON();
var jsonLabel = jsonparse.decode(response);
for(var i = 0; i < jsonLabel['executions'].length; i++)
	{
	var obj = jsonLabel['executions'][i];
	for(var key in obj)
		{
		if(key == 'configuration_id')
			{
			workflow.scratchpad.configuration_id = obj[key];
		}
		if(key == 'configuration_url')
			{
			workflow.scratchpad.configuration_url = obj[key];
		}
	}
}

var utils = new x_snc_skytap_demo.SWSSalesDemoIntegrationUtils();
var params = '';
params = 'title=' + current.variables.title;
params += ',templateid=' + current.cat_item.skytap_template.id;
params += ',starttime=' + utils.getDateTimeFormatForRest(current.variables.start_date);
params += ',endtime=' + utils.getDateTimeFormatForRest(current.variables.end_date);
params += ',newname=' + current.variables.new_configuration_name;
params += ',timezone=UTC,delete_at_end=true';
workflow.scratchpad.params = params;

